package Trie;

import java.util.ArrayList;
import java.util.Arrays;
import java.util.Collections;
import java.util.List;

public class SearchSuggestionsSystem {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String[] products = {
				"",
				"",
				"",
				"" };
		String searchWord = "";
		System.out.println(suggestedProducts1(products, searchWord));
		// suggestedProducts(products, searchWord);
	}

	static TrieNode root = new TrieNode();

	public static List<List<String>> suggestedProducts(String[] products, String searchWord) {
		List<List<String>> outList = new ArrayList<List<String>>();
		WordFilter(products);

		TrieNode it = root;

		for (char c : searchWord.toCharArray()) {
			// System.out.println(c);
			int charIndex = c - 'a';

			if (it.children[charIndex] == null) {
				it = it.children[charIndex] = new TrieNode();
				outList.add(new ArrayList<>());
			} else {
				it = it.children[charIndex];
				Collections.sort(it.commonWord);
				if (it.commonWord.size() > 3) {
					outList.add(it.commonWord.subList(0, 3));
				} else {
					outList.add(it.commonWord);
				}
				// System.out.println(it.commonWord);
			}

		}
		// System.out.println(outList.subList(339,392));
		return outList;

	}

	public static void WordFilter(String[] words) {
		for (String word : words) {
			TrieNode it = root;
			for (char c : word.toCharArray()) {

				int charIndex = c - 'a';

				if (it.children[charIndex] == null) {
					it.children[charIndex] = new TrieNode();
				}
				it = it.children[charIndex];
				it.commonWord.add(word);
				// System.out.println(c +" - "+ it.commonWord);

			}
			// System.out.println(it.commonWord);
			// it.children['{' - 'a'] = new TrieNode();

		}
	}

	public static class TrieNode {

		TrieNode[] children;
		ArrayList<String> commonWord;

		public TrieNode() {
			children = new TrieNode[27];
			commonWord = new ArrayList<>();
		}

	}

	public static List<List<String>> suggestedProducts1(String[] products, String searchWord) {
		List<List<String>> outList = new ArrayList<List<String>>();
		Arrays.sort(products);
		ArrayList<String> list1;
		String st = "";
		for (char c : searchWord.toCharArray()) {
			st+=c;
			list1 = new ArrayList<>();
			for (int j = 0; j < products.length; j++) {
				if (products[j].length()>=st.length() && products[j].substring(0, st.length()).equals(st)) {
					if (list1.size() < 3) {
						list1.add(products[j]);
					} else {
						break;
					}
				}
			}
			outList.add(list1);

		}

		//System.out.println(outList);
		return outList;

	}

}
